mapper.update:
UPDATE PRODUCT_INQUIRY
SET 
    TITLE = #{title},
    QUESTION_CONTENT = #{questionContent},
    PRIVATE_YN = #{privateYn}
WHERE PRODUCT_INQUIRY_NO = ${productInquiryNo}
AND MEMBER_NO = ${memberNo}

UPDATE PRODUCT_INQUIRY
SET
    DELETED_YN = 'Y'
WHERE PRODUCT_INQUIRY_NO = ${productInquiryNo}
AND MEMBER_NO = ${memberNo}
